}

function Forecast(props){
  return <div className = "grid-row">

  </div>
}

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      location: "",
      weather: undefined,
    };

    this.handleClick = async () => {
      const data = await getWeather(weatherApiBuilder(this.state.location, 3));
      this.setState({weather:data})
    }

  }


  render(){
    return <div>

            please enter city name, coordinates, or US/UK zip code:
            <input
              type="text"
              required
              value={this.state.location}
              onChange ={(e) => this.setState({location: e.target.value})} 
              /> 
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>


              {typeof this.state.weather != undefined ?
              <div>
              <div> {this.state.weather.location.name}, {this.state.weather.location.region}, {this.state.weather.location.country} </div>
              <div> {this.state.weather.current.temp_f} Degrees Fahrenheit and {this.state.weather.current.humidity}% Humidity</div>
              <div> 
                Date: {this.state.weather.forecast.forecastday[0].date}
                Max: {this.state.weather.forecast.forecastday[0].day.mintemp_f}
                Min: {this.state.weather.forecast.forecastday[0].day.mintemp_f}
                Avg. Humidity: {this.state.weather.forecast.forecastday[0].day.avghumidity}
              </div>
              <div>
                Date: {this.state.weather.forecast.forecastday[1].date}
                Max: {this.state.weather.forecast.forecastday[1].day.mintemp_f}
                Min: {this.state.weather.forecast.forecastday[1].day.mintemp_f}
                Avg. Humidity: {this.state.weather.forecast.forecastday[1].day.avghumidity}
              </div>
              <div>
                Date: {this.state.weather.forecast.forecastday[2].date}
                Max: {this.state.weather.forecast.forecastday[2].day.mintemp_f}
                Min: {this.state.weather.forecast.forecastday[2].day.mintemp_f}
                Avg. Humidity: {this.state.weather.forecast.forecastday[2].day.avghumidity}
                </div>
                </div>
                : ""}

      </div>
  }
}


export default App;


