import React from 'react';

// Helper Functions

function weatherApiBuilder(location){
  let type = "forecast"
  let key = "ba47e033e3c54cd5b8720248211507"
  let days = 3
  let aqi = "yes"
  return `http://api.weatherapi.com/v1/"${type}.json?key=${key}&q=${location}&days=${days}&aqi=${aqi}}&alerts=no`;
}

async function getWeather(apiUrl){
  try {
    const response = await fetch(apiUrl);
    if (!response.ok) {
      const message = `An error has occurred: ${response.status}`;
      throw new Error(message);
    }
    else {
      const data = await response.json();
      return data
    }
  }
  catch(error){console.log(error)}
}

// Components

function Location (props){
  return <div> {props.data.weather.location.name}, {props.data.weather.location.region}, {props.data.weather.location.country} </div>
}

function Current (props){
  return <div> {props.data.weather.current.temp_f} Degrees Fahrenheit and {props.data.weather.current.condition.text} </div>
}

function Forecast (props){
  return (<div>
    <div> 
    Date: {props.data.weather.forecast.forecastday[0].date}
    Max: {props.data.weather.forecast.forecastday[0].day.maxtemp_f}
    Min: {props.data.weather.forecast.forecastday[0].day.mintemp_f}
    Avg. Humidity: {props.data.weather.forecast.forecastday[0].day.avghumidity}
    <img src={props.data.weather.forecast.forecastday[0].day.condition.icon} alt=""/>
  </div>
  <div>
    Date: {props.data.weather.forecast.forecastday[1].date}
    Max: {props.data.weather.forecast.forecastday[1].day.maxtemp_f}
    Min: {props.data.weather.forecast.forecastday[1].day.mintemp_f}
    Avg. Humidity: {props.data.weather.forecast.forecastday[1].day.avghumidity}
    <img src={props.data.weather.forecast.forecastday[1].day.condition.icon} alt=""/>
  </div>
  <div>
    Date: {props.data.weather.forecast.forecastday[2].date}
    Max: {props.data.weather.forecast.forecastday[2].day.maxtemp_f}
    Min: {props.data.weather.forecast.forecastday[2].day.mintemp_f}
    Avg. Humidity: {props.data.weather.forecast.forecastday[2].day.avghumidity}
    <img src={props.data.weather.forecast.forecastday[2].day.condition.icon} alt=""/>
    </div>
    </div>)}

// App

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      location: "",
      weather: "",
    };

    this.handleClick = async () => {
      const data = await getWeather(weatherApiBuilder(this.state.location, 3));
      this.setState({weather:data})
    }

  }

  render(){

    return <div>

            please enter city name, coordinates, or US/UK zip code:
            <input
              type="text"
              required
              value={this.state.location}
              onChange ={(e) => this.setState({location: e.target.value})} 
              /> 
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>

            {(typeof this.state.weather.location != "undefined") ?
            (<div>
            <Location data={this.state}/>
            <Current data={this.state}/>
            <Forecast data={this.state}/>
            </div>)
            : ("")}

      </div>

  }
}


export default App;
