import './App.css';
import { useState } from 'react';
import axios from 'axios';

function App() {
  const apiKey = "eea9891349d17d031cbf7b10b3d7e189"
  const [city, updateCity] = useState("London");
  const [temperature, updateTemperature] = useState("");
  const onSubmit = (city) => {
    axios({
      method: "GET",
      url: `http://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`
    })
    .then((res) => {
      console.log(res)
      updateTemperature(res)
    })
    .catch((err) => console.log(err))
  }

  return (
    <div className="App">
      <form onSubmit={onSubmit}>
          <input type="text" value={city} onChange={(e) => updateCity(e.target.value)}/>
          <input type="submit"/>
      </form>
      <h3> {temperature}</h3>
    </div>
  );
}

export default App;

