// the app broke when i made "weather" the entire json file, unsure how to access it - maybe consolelog?
import React from 'react';

function apiBuilder(location, days){
  //days req. ? 4>x>1
  return `http://api.weatherapi.com/v1/forecast.json?key=ba47e033e3c54cd5b8720248211507&q=${location}&days=${days}&aqi=yes&alerts=no`;
}

function Location(props){
  return <div> {props.data.display_name}, {props.data.display_region}, {props.data.display_country} </div>;
}

// function Current(props){
//   return <div> {props.data.weather.current.temp_f} Degrees Fahrenheit and {props.data.weather.current.humidity}% Humidity</div>
// }

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      location: "",
      apiKey: "ba47e033e3c54cd5b8720248211507",
      apiUrl: "http://api.weatherapi.com/v1/",

      display_name: "Oakland",
      display_region: "California",
      display_country: "United States of America",

      weather: [] //full api response, see https://www.weatherapi.com/api-explorer.aspx#forecast

    };

    this.handleClick = () => {

      fetch(apiBuilder(this.state.location, 4)) 
        .then(res => res.json())
        .then(data => this.setState({
          display_name: data.location.name,
          display_region: data.location.region,
          display_country: data.location.country,
          weather : data
        }))
       }

  }
  render(){
    return <div>
            <Location data={this.state}/>
            please enter city name, coordinates or US/UK zip code:
            <input
              type="text"
              required
              value={this.state.location}
              onChange ={(e) => this.setState({location: e.target.value})} 
              /> 
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>
            {this.state.weather.current.temp_f}
            {/* <Current data={this.state}/> */}
      </div>
  }
}


export default App;



// forecast : one api call -> coordinates; 5 days -> list by 3 hours
