    })
    .then((res) => {
      console.log(res)
      updateTemperature(res)
    })
    .catch((err) => console.log(err))
  }

  return (
    <div className="App">
      <form onSubmit={onSubmit}>
          <input type="text" value={city} onChange={(e) => updateCity(e.target.value)}/>
          <input type="submit"/>
      </form>
      <h3> {temperature}</h3>
    </div>
  ); */

  return (
    <div className = "App">
      <form>
        <input value={city} onChange={e => setCity(e.target.value)}></input>
        <button onClick={onClick}> Submit </button>
      </form>
      <h3> {temperature} Degrees Fahrenheit</h3>
    </div>
  )
}

