import React from 'react';

function apiBuilder(location, days){
  //days req. ? 4>x>1
  return `http://api.weatherapi.com/v1/forecast.json?key=ba47e033e3c54cd5b8720248211507&q=${location}&days=${days}&aqi=yes&alerts=no`;
}

function Location(props){
  return <div> {props.data.display_location} </div>;
}

function Current(props){
  return <div> {props.data.temperature} Degrees Fahrenheit and {props.data.humidity}% Humidity</div>
}

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      location: "",
      apiKey: "ba47e033e3c54cd5b8720248211507",
      apiUrl: "http://api.weatherapi.com/v1/",

      display_location: "Oakland",

      temperature: "",
      humidity: "",

    };

    this.handleClick = () => {

      this.setState({
        display_location: this.state.location
      })

      fetch(apiBuilder(this.state.location,this.state.days))
        .then(res => res.json())
        .then(data => {
          this.setState({temperature: data.current.temp_f});
          this.setState({humidity: data.current.humidity});
          })
    };
  }
  render(){
    return <div>
            <Location data={this.state}/>
            <input
              type="text"
              required
              value={this.state.location}
              onChange ={(e) => this.setState({location: e.target.value})} 
              /> 
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>
            <Current data={this.state}/>
      </div>
  }
}


export default App;



// forecast : one api call -> coordinates; 5 days -> list by 3 hours
