import React from 'react';


function Location(props){
  return <div> {props.data.display_city}, {props.data.display_state}, {props.data.display_country} </div>;
}

function Current(props){
  return <div> {props.data.temperature} Degrees Fahrenheit and {props.data.humidity}% Humidity</div>
}

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      city: "",
      state: "",
      country: "",
      apiKey: "eea9891349d17d031cbf7b10b3d7e189",

      display_city: "Oakland",
      display_state: "CA",
      display_country: "USA",

      temperature: "",
      humidity: "",

    };

    this.handleClick = () => {

      this.setState({
        display_city: this.state.city,
        display_state: this.state.state,
        display_country: this.state.country
      })

      fetch(`http://api.openweathermap.org/data/2.5/weather?q=${this.state.city},${this.state.state},${this.state.country}&appid=${this.state.apiKey}`)
        .then(res => res.json())
        .then(data => {
          this.setState({temperature:(((data.main.temp -273.15)*1.8)+32).toFixed(1)});
          this.setState({humidity: data.main.humidity});
          })
    };
  }
  render(){
    return <div>
            <Location data={this.state}/>
            <input
              type="text"
              required
              value={this.state.city}
              onChange ={(e) => this.setState({city: e.target.value})} 
              /> 
            <input
              type="text"
              required
              value={this.state.state}
              onChange ={(e) => this.setState({state: e.target.value})} 
              />
            <input
              type="text"
              required
              value={this.state.country}
              onChange ={(e) => this.setState({country: e.target.value})} 
              />
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>
            <Current data={this.state}/>
      </div>
  }
}


export default App;



// forecast : one api call -> coordinates; 5 days -> list by 3 hours
