// the app broke when i made "weather" the entire json file, unsure how to access it - maybe consolelog?
import React from 'react';

function apiBuilder(location, days){
  //days req. ? 4>x>1
  return `http://api.weatherapi.com/v1/forecast.json?key=ba47e033e3c54cd5b8720248211507&q=${location}&days=${days}&aqi=yes&alerts=no`;
}

function Location(props){
  return <div> {props.data.display_name}, {props.data.display_region}, {props.data.display_country} </div>;
}
//"display" data so location refresh onclick not on input
function Current(props){
  return <div> {props.data.weather["temp_f"]} Degrees Fahrenheit and {props.data.weather["temp_f"]}% Humidity</div>
}

class App extends React.Component {

  constructor(props){

    super(props);

    this.state = {
      location: "",
      apiKey: "ba47e033e3c54cd5b8720248211507",
      apiUrl: "http://api.weatherapi.com/v1/",

      //data structure same as api json

      display_location:{
        name:"Oakland",
        region: "CA",
        country: "USA"
      },
      current:{
        temp_f: "",
        humidity: ""
      },
      forecast:{
        forecastday:[
          {
            day:{
              mintemp_f: "",
              maxtemp_f: "",
              avghumidity: "",
              condition:{
                text: ""
              }
            }
          },
          {
            day:{
              mintemp_f: "",
              maxtemp_f: "",
              avghumidity: "",
              condition:{
                text: ""
              }
            }
          },
          {
            day:{
              mintemp_f: "",
              maxtemp_f: "",
              avghumidity: "",
              condition:{
                text: ""
              }
            }
          },
          {
            day:{
              mintemp_f: "",
              maxtemp_f: "",
              avghumidity: "",
              condition:{
                text: ""
              }
            }
          },
        ]
      }

      // weather: [] //full api response, see https://www.weatherapi.com/api-explorer.aspx#forecast

    };

    this.handleClick = () => {

      fetch(apiBuilder(this.state.location, 4)) 
        .then(res => res.json())
        .then(data => this.setState({
          display_location:{
            name: data.location.name,
            region: data.location.region,
            country: data.location.country,
          },
          current:{
            temp_f: data.current.temp_f,
            humidity: data.current.humidity
          },
          forecast:{
            forecastday:[
              {
                day:{
                  mintemp_f: data.forecast.forecastday[0].day.mintemp_f,
                  maxtemp_f: data.forecast.forecastday[0].day.maxtemp_f,
                  avghumidity: data.forecast.forecastday[0].day.avghumidity,
                  condition: {
                    text: data.forecast.forecastday[0].day.condition.text,
                  }
                }
              },
              {
                day:{
                  mintemp_f: data.forecast.forecastday[1].day.mintemp_f,
                  maxtemp_f: data.forecast.forecastday[1].day.maxtemp_f,
                  avghumidity: data.forecast.forecastday[1].day.avghumidity,
                  condition: {
                    text: data.forecast.forecastday[1].day.condition.text,
                  }
                }
              },
              {
                day:{
                  mintemp_f: data.forecast.forecastday[2].day.mintemp_f,
                  maxtemp_f: data.forecast.forecastday[2].day.maxtemp_f,
                  avghumidity: data.forecast.forecastday[2].day.avghumidity,
                  condition: {
                    text: data.forecast.forecastday[2].day.condition.text,
                  }
                }
              },
              {
                day:{
                  mintemp_f: data.forecast.forecastday[3].day.mintemp_f,
                  maxtemp_f: data.forecast.forecastday[3].day.maxtemp_f,
                  avghumidity: data.forecast.forecastday[3].day.avghumidity,
                  condition: {
                    text: data.forecast.forecastday[3].day.condition.text,
                  }
                }
              },
            ]
          }
        }))
       }

  }
  render(){
    return <div>
            <Location data={this.state}/>
            please enter city name, coordinates or US/UK zip code:
            <input
              type="text"
              required
              value={this.state.location}
              onChange ={(e) => this.setState({location: e.target.value})} 
              /> 
            <button 
              onClick= {() => this.handleClick()}>
                Submit
            </button>
            <Current data={this.state}/>
      </div>
  }
}


export default App;



